// Expecting items like:
// { metric, current_period, previous_period, current_value, previous_value, delta_abs, delta_pct }

const fmt2 = n => (n == null || n === '' ? '' : Number(n).toFixed(2));
const pct  = n => (n == null || n === '' ? '' : (Number(n) * 100).toFixed(2) + '%');

const first = $items()[0]?.json || {};
const curLabel  = first.current_period  || 'Current';
const prevLabel = first.previous_period || 'Previous';

const rows = $items().map(({ json }) => {
  const deltaAbs = Number(json.delta_abs);
  const color = deltaAbs > 0 ? '#2e7d32' : (deltaAbs < 0 ? '#c62828' : '#444');
  const sign  = deltaAbs > 0 ? '+' : '';

  return `
    <tr>
      <td style="padding:8px;border-bottom:1px solid #eee;">${json.metric}</td>
      <td style="padding:8px;text-align:right;border-bottom:1px solid #eee;">${fmt2(json.current_value)}</td>
      <td style="padding:8px;text-align:right;border-bottom:1px solid #eee;">${fmt2(json.previous_value)}</td>
      <td style="padding:8px;text-align:right;border-bottom:1px solid #eee;color:${color};">${sign}${fmt2(deltaAbs)}</td>
      <td style="padding:8px;text-align:right;border-bottom:1px solid #eee;color:${color};">${sign}${pct(json.delta_pct)}</td>
    </tr>`;
}).join('');

const html = `
<table style="border-collapse:collapse;width:100%;max-width:520px;font-family:Arial,Helvetica,sans-serif;font-size:14px;">
  <thead>
    <tr>
      <th style="text-align:left;padding:8px;border-bottom:1px solid #ddd;">Metric</th>
      <th style="text-align:right;padding:8px;border-bottom:1px solid #ddd;">${curLabel}</th>
      <th style="text-align:right;padding:8px;border-bottom:1px solid #ddd;">${prevLabel}</th>
      <th style="text-align:right;padding:8px;border-bottom:1px solid #ddd;">Δ (abs)</th>
      <th style="text-align:right;padding:8px;border-bottom:1px solid #ddd;">Δ (%)</th>
    </tr>
  </thead>
  <tbody>${rows}</tbody>
</table>`;

return [{ json: { html } }];
